module.exports = {

  /**
   * Description
   * @method newInvalidAsn1Error
   * @param {} msg
   * @return e
   */
  newInvalidAsn1Error: function(msg) {
    var e = new Error();
    e.name = 'InvalidAsn1Error';
    e.message = msg || '';
    return e;
  }

};
