'use strict';

module.exports = filter;
/**
 * Description
 * @method filter
 * @param {} name
 * @param {} str
 * @param {} options
 * @return 
 */
function filter(name, str, options) {
  if (typeof filter[name] === 'function') {
    return filter[name](str, options);
  } else {
    throw new Error('unknown filter ":' + name + '"');
  }
}
